mapper.insert:
INSERT INTO departments
(
dept_id
, dept_name
, parent_dept_id
, phone_number
, is_location
)
VALUES
(
#{deptId}
, #{deptName}
, #{parentDeptId}
, #{phoneNumber}
, #{isLocation}
)

mapper.update:
UPDATE departments
SET dept_name = #{deptName}
, parent_dept_id = #{parentDeptId}
, phone_number = #{phoneNumber}
WHERE dept_id = #{deptId}
