mapper.insert:
INSERT INTO employees
			(
				employee_id
				, last_name
				, email
				, hire_date
				, job_id
			)
			VALUES
			(
				(SELECT MAX(employee_id) + 1 FROM employees)
				, #{lastName}
				, #{email}
				, sysdate
				, #{jobId}
			)

mapper.update:
UPDATE employees
SET
    first_name = #{firstName}
    , salary = #{salary}
WHERE employee_id = #{employeeId}
